import './menu-item.styles.scss';

const MenuItem = ({title, imageUrl, size}) => (
    <div style={{
        backgroundImage: `url(${imageUrl})`
    }} 
    className="menu-item">
                <div classname="content">
                    <h1 className="title">{title}</h1>
                    <span className="subtitle">Buy Now</span>
                </div>
            </div>
);

export default MenuItem;
